import React from 'react';
import axios from "axios";



class JokeListClass extends React.Component {
    constructor(props) {
        super(props);
        this.state = {jokes: [] }
        this.getNewJokes = this.getNewJokes.bind(this)
        this.resetVotes = this.resetVotes.bind(this);
        this.toggleLock = this.toggleLock.bind(this);
        this.vote = this.vote.bind(this);




    }

    componentDidMount() {
        console.log(this.state.jokes.length)
        if (this.state.jokes.length <= 0) this.getJokes();
      }
      componentDidUpdate() {
        if (this.state.jokes.length <= 0) this.getJokes();
      }


async getJokes() {




    let j = [...this.state.jokes];
   let numJokesToGet = 10
//    let seenJokes = new Set(j.map(j => j.id));

    while (j.length < numJokesToGet) {
    let res = await axios.get("https://icanhazdadjoke.com", {
        headers: { Accept: "application/json" }
      });
      let { status, ...jokeObj } = res.data;
      console.log(jokeobj)

      j.push(jokeObj.joke)


    }
    this.setState({jokes: j})
    console.log(this.state.jokes)





}
getNewJokes () {
    this.setState({jokes: [] })
    console.log(this.state.jokes)


}

resetVotes() {
    window.localStorage.setItem("jokeVotes", "{}");
    this.setState(st => ({
      jokes: st.jokes.map(joke => ({ ...joke, votes: 0 }))
    }));
  }

  /* change vote for this id by delta (+1 or -1) */

  vote(id, delta) {
    let jokeVotes = JSON.parse(window.localStorage.getItem("jokeVotes"));
    jokeVotes[id] = (jokeVotes[id] || 0) + delta;
    window.localStorage.setItem("jokeVotes", JSON.stringify(jokeVotes));
    this.setState(st => ({
      jokes: st.jokes.map(j =>
        j.id === id ? { ...j, votes: j.votes + delta } : j
      )
    }));
  }

  toggleLock(id) {
    this.setState(st => ({
      jokes: st.jokes.map(j => (j.id === id ? { ...j, locked: !j.locked } : j))
    }));
  }






    // generateNewJokes() {
