    render() {

        return (
            <div className="JokeList">
            <button className="JokeList-getmore" onClick={this.getNewJokes} >
              Get New Jokes
            </button>
            {this.state.jokes.map(j => (
                <h1>{j}</h1>
        ))}


          </div>
        )
    }
}
export default JokeListClass;
